class Solution:
    def removeDuplicates(self, s: str, k: int) -> str:
        n = len(s)
        s = list(s)
        i, j = n-1, n - 2
        while j >= 0:
            a, b = s[i], s[j]
            if s[i] != s[j]:
                i, j = j, j - 1
            else:
                j -= 1
                if i - j == k:
                    write = j+1
                    for m in range(i + 1, n):
                        s[write] = s[m]
                        write, m = write+1, m + 1
                    n -= k
                    j = n - 2
                    i = n - 1
        return "".join(s[:n])


s = ""
k1 = 1000
expected = ""
sol = Solution()
#s = "pbbcggttciiippobboaais"
#expected = "ps"
print(sol.removeDuplicates(s, k1) == expected)
for i in range(min(len(s), len(expected))):
    if s[i] != expected[i]:
        print(i)
